mapper.update:
UPDATE t_document
title = #{title}, content = #{content}, source = #{source}, document_type = #{documentType}, status = #{status}, batch_id = #{batchId}, parent_id = #{parentId}, update_time = #{updateTime}, metadata = #{metadata}, position = #{position}, vectorized = #{vectorized}, error_message = #{errorMessage},
WHERE id = #{id}

mapper.select:
SELECT 
FROM t_document


        AND title LIKE CONCAT('%', #{title}, '%')


        AND source = #{source}


        AND document_type = #{documentType}


        AND status = #{status}


        AND batch_id = #{batchId}


        AND parent_id = #{parentId}


        AND vectorized = #{vectorized}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}


ORDER BY create_time DESC
